    render() {
        /*
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        if(this.state.noRest){
            return <Navigate to={"/notallowed"}/>
        }*/
        return (
            <div className="App">
                <header className="header">
                    <h1 className="title">Add a new location!</h1>
                    <form onSubmit={this.handleAddLocation}>
                        <div className="input">
                            <label>Name </label>
                            <input type="text" name="name" value={this.state.name} onChange={this.handleChangeName} required />
                        </div>
                        <div className="input">
                            <label>Address </label>
                            <input type="text" name="address" value={this.state.address} onChange={this.handleChangeAddress} required />
                        </div>
                        <div className="input">
                            <label>Description </label>
                            <input type="text" name="description" value={this.state.description} onChange={this.handleChangeDescription} required />
                        </div>
                        <div className="input">
                            <label>Hours </label>
                            <input type="text" name="hours" value={this.state.hours} onChange={this.handleChangeHours} required />
                        </div>
                        <input className="button" type="submit" value="Add" />
                        <button className="button">
                            <Link className="link" to="/creatorprofile">Back</Link>
                        </button>
                    </form>
                </header>
            </div>
        )
    }

}
export default AddLocation;
