import './App.css';
import './Admin.css';
import React, {Component} from "react";
import {Link, Navigate} from "react-router-dom";

class AdminProfile extends Component {

    constructor(props) {
        super(props);
        this.state = {error:''}
    }
    async componentDidMount() {
        const token = localStorage.getItem("jwt")
        const requestOptions = {
            method: 'GET',
            headers:{   'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        };
        fetch('http://localhost:8080/adminprofile',requestOptions)
            .then(async response=>{
                if(response.status===403 || response.status===401){
                    this.setState({error: true});
                }else {
                    this.setState({error: false});
                }
            })
    }

    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div className="Start">
                <header className="header">
                    <h1 className="title">Welcome,{localStorage.getItem("user")}!</h1>
                    <div className="tab">
                        <button className="tablinks" onClick="openCity(event, 'London')">London</button>
                        <button className="tablinks" onClick="openCity(event, 'Paris')">Paris</button>
                        <button className="tablinks" onClick="openCity(event, 'Tokyo')">Tokyo</button>
                    </div>

                    <div id="London" className="tabcontent">
                        <h3>London</h3>
                        <p>London is the capital city of England.</p>
                    </div>

                    <div id="Paris" className="tabcontent">
                        <h3>Paris</h3>
                        <p>Paris is the capital of France.</p>
                    </div>

                    <div id="Tokyo" className="tabcontent">
                        <h3>Tokyo</h3>
                        <p>Tokyo is the capital of Japan.</p>
                    </div>
                </header>
            </div>
        )
    };
}

export default AdminProfile;
