import './App.css';
import './PopUp.css';
import React, {Component} from "react";
import {Link, Navigate} from 'react-router-dom'


class ViewPlanner extends Component {
    constructor(props) {
        super(props);
        this.state = {planner: null, error: false};
        this.closeForm = this.closeForm.bind(this);
        this.openForm = this.openForm.bind(this);
    }

    async componentDidMount() {
        console.log(JSON.parse(localStorage.getItem("planner")))
        this.setState({planner: JSON.parse(localStorage.getItem("planner"))},() => {
        });
        const token = localStorage.getItem("jwt")
        const requestOptions = {
            method: 'GET',
            headers:{   'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        };
        fetch('http://localhost:8080/viewplanner',requestOptions)
            .then(async response=>{
                if(response.status===403 || response.status===401){
                    this.setState({error: true});
                }else {
                    this.setState({error: false});
                }
            })
    }
    openForm() {
        document.getElementById("myForm").style.display = "block";
    }

    closeForm() {
        document.getElementById("myForm").style.display = "none";
    }

    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div className="Start">
                <header className="header">
                    <h1 className="title">{this.state.planner?.name}</h1>
                    <button className="open-button" onClick={this.state.openForm}>Chat</button>
                    <div className="chat-popup" id="myForm">
                        <div className="chat-popup" id="myForm">
                            <form action="/action_page.php" className="form-container">
                                <h1>Chat</h1>

                                <label htmlFor="msg"><b>Message</b></label>
                                <textarea placeholder="Type message.." name="msg" required></textarea>

                                <button type="submit" className="btn">Send</button>
                                <button type="button" className="btn cancel" onClick="closeForm()">Close</button>
                            </form>
                        </div>
                    </div>
                </header>
            </div>
        )
    }
}
export default ViewPlanner;
