import './App.css';
import './Admin.css';
import React, {Component} from "react";
import {Link, Navigate} from "react-router-dom";

class AdminProfile extends Component {

    constructor(props) {
        super(props);
        this.state = {error:''}
    }
    async componentDidMount() {
        const token = localStorage.getItem("jwt")
        const requestOptions = {
            method: 'GET',
            headers:{   'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        };
        fetch('http://localhost:8080/adminprofile',requestOptions)
            .then(async response=>{
                if(response.status===403 || response.status===401){
                    this.setState({error: true});
                }else {
                    this.setState({error: false});
                }
            })
    }
     openCity( cityName) {
        // Declare all variables
        let i, tabcontent, tablinks;

        // Get all elements with class="tabcontent" and hide them
        tabcontent = document.getElementsByClassName("tabcontent");
        for (i = 0; i < tabcontent.length; i++) {
            tabcontent[i].style.display = "none";
        }

        // Get all elements with class="tablinks" and remove the class "active"
        tablinks = document.getElementsByClassName("tablinks");
        for (i = 0; i < tablinks.length; i++) {
            tablinks[i].className = tablinks[i].className.replace(" active", "");
        }

        // Show the current tab, and add an "active" class to the link that opened the tab
        document.getElementById(cityName).style.display = "block";
        //evt.currentTarget.className += " active";
    }

    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div>
                <header className="header">
                    <h1 className="title">Welcome,{localStorage.getItem("user")}!</h1>
                    <div className="tab">
                        <button className="tablinks" onClick={this.openCity.bind(null, 'London')}>London</button>
                        <button className="tablinks" onClick={this.openCity.bind(null, 'Paris')}>Paris</button>
                        <button className="tablinks" onClick={this.openCity.bind(null, 'Tokyo')}>Tokyo</button>
                    </div>

                    <div id="London" className="tabcontent">
                        <h3>London</h3>
                        <p>London is the capital city of England.</p>
                    </div>

                    <div id="Paris" className="tabcontent">
                        <h3>Paris</h3>
                        <p>Paris is the capital of France.</p>
                    </div>

                    <div id="Tokyo" className="tabcontent">
                        <h3>Tokyo</h3>
                        <p>Tokyo is the capital of Japan.</p>
                    </div>
                </header>
            </div>
        )
    };
}

export default AdminProfile;
