import './App.css';
import React, {Component} from "react";
import {Link} from 'react-router-dom'
class ViewPlanners extends Component {
    constructor(props) {
        super(props);
        this.state = {planners: [], error: false};
    }

    async componentDidMount() {
        const token = localStorage.getItem("jwt")
        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        };
        fetch('http://localhost:8080/viewplanners/' + localStorage.getItem("user"), requestOptions)
            .then(async response => {
                if (response.status === 403 || response.status === 401) {
                    this.setState({error: true});
                } else {
                    this.setState({error: false});
                    const d = await response.json();
                    this.setState({planners: d});
                    console.log(d);
                }
            })
    }

    render() {
        return (
            <div className="Start">
                <header className="header">
                    <h1 className="title">My planners</h1>
                    <ul>
                        {this.state.planners.map(l => {
                            return <li className="li"><Link className="link" onClick={this.clickLink.bind(null,l)} to="/viewlocation">{l.name}</Link></li>
                        })}
                    </ul>
                </header>
            </div>
        )
    }
}
export default ViewPlanners;
