import './App.css';
import React, {Component} from "react";
import {Link, Navigate} from "react-router-dom";
class ViewLocation extends Component {
    constructor(props) {
        super(props);
        this.state = {loc:null,error:false};
    }
    async componentDidMount() {
        console.log(JSON.parse(localStorage.getItem("location")))
        this.setState({loc: JSON.parse(localStorage.getItem("location"))},() => {
            console.log(this.state.loc);
        });
    }
    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div >
                <header className="header2">
                    <h1>{this.state.loc?.name}</h1>
                    <img src={this.state.loc?.image} />
                    <div className="address">Address: {this.state.loc?.address}</div>
                    <div>Hours: {this.state.loc?.hours}</div>
                    <div>Description: {this.state.loc?.description}</div>
                    <div>Country: {this.state.loc?.country}</div>
                    <div>Category: {this.state.loc?.locationType}</div>
                    <div>Score: {this.state.loc?.score}</div>

                </header>
            </div>
        )
    }

}
export default ViewLocation;
