import './App.css';
import React, {Component} from "react";
import {Link, Navigate} from "react-router-dom";
class ViewLocation extends Component {
    constructor(props) {
        super(props);
        this.state = {loc:null,error:false};
    }
    async componentDidMount() {
        this.setState({loc: localStorage.getItem("location")})
    }
    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div className="App">
                <header className="header">
                    <h1>{this.state.loc.name}</h1>
                    <h2>Address: {this.state.loc.address}</h2>
                    <h2>Hours: {this.state.loc.hours}</h2>
                    <h3>Description: {this.state.loc.description}</h3>
                </header>
            </div>
        )
    }

}
export default ViewLocation;
