import './App.css';
import React, {Component} from "react";
import {Link, Navigate} from "react-router-dom";
class ViewCreator extends Component {
    constructor(props) {
        super(props);
        this.state = {locations: [], error: false};
        this.handleChangeStatus = this.handleChangeStatus.bind(this);
        this.clickLink = this.clickLink.bind(this);
    }

    async componentDidMount() {
        const token = localStorage.getItem("jwt")
        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        };
        fetch('http://localhost:8080/viewcreator/' + localStorage.getItem("user"), requestOptions)
            .then(async response => {
                if (response.status === 403 || response.status === 401) {
                    this.setState({error: true});
                } else {
                    const d = await response.json();
                    this.setState({locations: d});
                    console.log(d);
                }
            })
    }
        clickLink(loc){
            localStorage.setItem("location",loc);
        }

    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div className="App">
                <header className="header">
                    <h1>View your locations</h1>
                    <ul className="ul">
                        {this.state.locations.map(l => {
                            return <li className="li"><Link className="link" onClick={this.clickLink(l)} to={"/"+l.name}>{l.name}</Link></li>
                        })}
                    </ul>
                </header>
            </div>
        )
    }

}
export default ViewCreator;
