import './App.css';
import React, {Component} from "react";
import {Link, Navigate} from "react-router-dom";

class UserProfile extends Component {

    constructor(props) {
        super(props);
        this.state = {error:''}
    }
    async componentDidMount() {
        const token = localStorage.getItem("jwt")
        const requestOptions = {
            method: 'GET',
            headers:{   'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        };
        fetch('http://localhost:8080/userprofile',requestOptions)
            .then(async response=>{
                if(response.status===403 || response.status===401){
                    this.setState({error: true});
                }else {
                    this.setState({error: false});
                }
            })
    }

    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div className="Start">
                <header className="header">
                    <h1 className="title">Welcome,{localStorage.getItem("user")}!</h1>
                    <button className="button">
                        <Link className="link" to="/explore">Explore</Link>
                    </button>
                    <button className="button">
                        <Link className="link" to="/wishlist">My wishlist</Link>
                    </button>
                    <button className="button">
                        <Link className="link" to="/planners">My planners</Link>
                    </button>
                </header>
            </div>
        )
    };
}

export default UserProfile;
