import './App.css';
import React, {Component} from "react";
import {Link, Navigate} from "react-router-dom";
import JsPDF from 'jspdf';
import html2canvas from "html2canvas";

class ViewLocation extends Component {
    constructor(props) {
        super(props);
        this.state = {loc:null,error:false,img:''};
    }
    async componentDidMount() {
        console.log(JSON.parse(localStorage.getItem("location")))
        this.setState({loc: JSON.parse(localStorage.getItem("location"))},() => {
            console.log(this.state.loc.image);
            this.setState({img:this.state.loc.image});
        });


    }
    exportPDF(){
        const pdf = new JsPDF("portrait", "pt", "a4");
        html2canvas(document.querySelector("#report")).then(data=>{
            const img = data.toDataURL("image/png");
            const imgProperties = pdf.getImageProperties(img);
            const pdfWidth = pdf.internal.pageSize.getWidth();
            const pdfHeight = (imgProperties.height * pdfWidth) / imgProperties.width;
            pdf.addImage(img, "PNG", 0, 0, pdfWidth, pdfHeight);
            pdf.save(localStorage.getItem("restaurant")+"-Menu.pdf");}
        )

    }
    render() {
        if (this.state.error) {
            return <Navigate to={"/accessdenied"}/>
        }
        return (
            <div >
                <header className="header2">
                    <h1>{this.state.loc?.name}</h1>
                    <img className="photo" src={window.location.origin + '/img/'+this.state.img} />
                    <div className="address">Address: {this.state.loc?.address}</div>
                    <div>Hours: {this.state.loc?.hours}</div>
                    <div>Description: {this.state.loc?.description}</div>
                    <div>Country: {this.state.loc?.country}</div>
                    <div>Category: {this.state.loc?.locationType}</div>
                    <div>Score: {this.state.loc?.score}</div>
                    <button className="button" onClick={this.exportPDF}>Export as PDF</button>

                </header>
            </div>
        )
    }

}
export default ViewLocation;
